import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const NomenclatureList = () => {
  const [nomenclatures, setNomenclatures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [syncStatus, setSyncStatus] = useState({});

  useEffect(() => {
    fetchNomenclatures();
  }, []);

  const fetchNomenclatures = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/nomenclature');
      setNomenclatures(response.data);
      setError(null);
    } catch (err) {
      setError('Ошибка при загрузке данных: ' + err.message);
      console.error('Ошибка при загрузке номенклатуры:', err);
    } finally {
      setLoading(false);
    }
  };

  const syncToIntBus = async (id) => {
    try {
      setSyncStatus(prev => ({ ...prev, [id]: 'loading' }));
      const response = await axios.post(`/api/nomenclature/${id}/sync`);
      setSyncStatus(prev => ({ ...prev, [id]: 'success' }));
      setTimeout(() => {
        setSyncStatus(prev => ({ ...prev, [id]: null }));
      }, 3000);
    } catch (err) {
      setSyncStatus(prev => ({ ...prev, [id]: 'error' }));
      console.error('Ошибка при отправке в IntBus:', err);
    }
  };

  if (loading) {
    return <div className="d-flex justify-content-center mt-5">
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Загрузка...</span>
      </div>
    </div>;
  }

  if (error) {
    return <div className="alert alert-danger" role="alert">{error}</div>;
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Список номенклатуры</h2>
        <div>
          <Link to="/nomenclature/new" className="btn btn-success me-2">
            <i className="bi bi-plus-circle"></i> Добавить новую
          </Link>
          <a href="/api/excel/export" className="btn btn-primary">
            <i className="bi bi-file-excel"></i> Экспорт в Excel
          </a>
        </div>
      </div>

      {nomenclatures.length > 0 ? (
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-primary">
              <tr>
                <th>ID</th>
                <th>Сокращение</th>
                <th>Краткое название</th>
                <th>Полное название</th>
                <th>Внутренний код</th>
                <th>Шифр</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {nomenclatures.map(item => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.abbreviation}</td>
                  <td>{item.shortName}</td>
                  <td>{item.fullName}</td>
                  <td>{item.internalCode}</td>
                  <td>{item.cipher}</td>
                  <td>
                    <div className="btn-group" role="group">
                      <Link to={`/nomenclature/edit/${item.id}`} className="btn btn-sm btn-warning">
                        Редактировать
                      </Link>
                      <Link to={`/nomenclature/delete/${item.id}`} className="btn btn-sm btn-danger ms-1">
                        Удалить
                      </Link>
                      <button 
                        onClick={() => syncToIntBus(item.id)}
                        className="btn btn-sm btn-info ms-1"
                        disabled={syncStatus[item.id] === 'loading'}
                      >
                        {syncStatus[item.id] === 'loading' ? (
                          <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                        ) : syncStatus[item.id] === 'success' ? (
                          <i className="bi bi-check-circle"></i>
                        ) : syncStatus[item.id] === 'error' ? (
                          <i className="bi bi-x-circle"></i>
                        ) : (
                          <i className="bi bi-arrow-up-circle"></i>
                        )}
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="alert alert-info">
          Номенклатура отсутствует. <Link to="/nomenclature/new">Добавить новую</Link>.
        </div>
      )}
    </div>
  );
};

export default NomenclatureList;
