import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const LSIForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [lsi, setLsi] = useState({
    name: '',
    description: ''
  });

  const isEditMode = !!id;

  useEffect(() => {
    if (isEditMode) {
      fetchLSI();
    }
  }, [id]);

  const fetchLSI = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/lsi/${id}`);
      setLsi(response.data);
      setError(null);
    } catch (err) {
      setError('Ошибка при загрузке данных: ' + err.message);
      console.error('Ошибка при загрузке ЛСИ:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLsi(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      setLoading(true);
      
      if (isEditMode) {
        await axios.put(`/api/lsi/${id}`, lsi);
      } else {
        await axios.post('/api/lsi', lsi);
      }
      
      navigate('/lsi');
    } catch (err) {
      setError('Ошибка при сохранении данных: ' + err.message);
      console.error('Ошибка при сохранении ЛСИ:', err);
      setLoading(false);
    }
  };

  if (loading && isEditMode) {
    return <div className="d-flex justify-content-center mt-5">
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Загрузка...</span>
      </div>
    </div>;
  }

  return (
    <div className="container">
      <div className="row">
        <div className="col-12">
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item"><Link to="/">Главная</Link></li>
              <li className="breadcrumb-item"><Link to="/lsi">ЛСИ</Link></li>
              <li className="breadcrumb-item active" aria-current="page">
                {isEditMode ? `Редактирование #${id}` : 'Создание'}
              </li>
            </ol>
          </nav>
        </div>
      </div>

      <div className="card">
        <div className="card-header bg-primary text-white">
          <h4 className="card-title mb-0">
            {isEditMode ? `Редактирование ЛСИ #${id}` : 'Создание новой ЛСИ'}
          </h4>
        </div>
        <div className="card-body">
          {error && (
            <div className="alert alert-danger" role="alert">{error}</div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Название</label>
              <input
                type="text"
                className="form-control"
                id="name"
                name="name"
                value={lsi.name}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Описание</label>
              <textarea
                className="form-control"
                id="description"
                name="description"
                rows="5"
                value={lsi.description}
                onChange={handleInputChange}
                required
              ></textarea>
            </div>
            
            <div className="d-flex justify-content-between">
              <Link to="/lsi" className="btn btn-secondary">Отмена</Link>
              <button type="submit" className="btn btn-primary" disabled={loading}>
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Сохранение...
                  </>
                ) : 'Сохранить'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LSIForm;
