import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const NomenclatureForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [nomenclature, setNomenclature] = useState({
    abbreviation: '',
    shortName: '',
    fullName: '',
    internalCode: '',
    cipher: '',
    ekpsCode: '',
    kvtCode: '',
    drawingNumber: '',
    typeOfNomenclature: ''
  });

  const isEditMode = !!id;

  useEffect(() => {
    if (isEditMode) {
      fetchNomenclature();
    }
  }, [id]);

  const fetchNomenclature = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/nomenclature/${id}`);
      setNomenclature(response.data);
      setError(null);
    } catch (err) {
      setError('Ошибка при загрузке данных: ' + err.message);
      console.error('Ошибка при загрузке номенклатуры:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNomenclature(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      setLoading(true);
      
      if (isEditMode) {
        await axios.put(`/api/nomenclature/${id}`, nomenclature);
      } else {
        await axios.post('/api/nomenclature', nomenclature);
      }
      
      navigate('/nomenclature', { replace: true });
    } catch (err) {
      setError('Ошибка при сохранении данных: ' + err.message);
      console.error('Ошибка при сохранении номенклатуры:', err);
      setLoading(false);
    }
  };
  
  const handleSendToIntBus = async () => {
    try {
      setLoading(true);
      setSuccessMessage(null);
      setError(null);
      
      const response = await axios.post(`/api/nomenclature/${id}/send-to-intbus`);
      setSuccessMessage('Данные успешно отправлены в IntBus');
    } catch (err) {
      setError('Ошибка при отправке данных в IntBus: ' + (err.response?.data || err.message));
      console.error('Ошибка при отправке данных в IntBus:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading && isEditMode) {
    return <div className="d-flex justify-content-center mt-5">
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Загрузка...</span>
      </div>
    </div>;
  }

  return (
    <div className="container">
      <div className="row">
        <div className="col-12">
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item"><Link to="/">Главная</Link></li>
              <li className="breadcrumb-item"><Link to="/nomenclature">Номенклатура</Link></li>
              <li className="breadcrumb-item active" aria-current="page">
                {isEditMode ? `Редактирование #${id}` : 'Создание'}
              </li>
            </ol>
          </nav>
        </div>
      </div>

      <div className="card">
        <div className="card-header bg-primary text-white">
          <h4 className="card-title mb-0">
            {isEditMode ? `Редактирование номенклатуры #${id}` : 'Создание новой номенклатуры'}
          </h4>
        </div>
        <div className="card-body">
          {error && (
            <div className="alert alert-danger" role="alert">{error}</div>
          )}
          
          {successMessage && (
            <div className="alert alert-success" role="alert">{successMessage}</div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="abbreviation" className="form-label">Сокращение</label>
                <input
                  type="text"
                  className="form-control"
                  id="abbreviation"
                  name="abbreviation"
                  value={nomenclature.abbreviation}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="shortName" className="form-label">Краткое название</label>
                <input
                  type="text"
                  className="form-control"
                  id="shortName"
                  name="shortName"
                  value={nomenclature.shortName}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            
            <div className="mb-3">
              <label htmlFor="fullName" className="form-label">Полное название</label>
              <input
                type="text"
                className="form-control"
                id="fullName"
                name="fullName"
                value={nomenclature.fullName}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="internalCode" className="form-label">Внутренний код</label>
                <input
                  type="text"
                  className="form-control"
                  id="internalCode"
                  name="internalCode"
                  value={nomenclature.internalCode}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="cipher" className="form-label">Шифр</label>
                <input
                  type="text"
                  className="form-control"
                  id="cipher"
                  name="cipher"
                  value={nomenclature.cipher}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-4 mb-3">
                <label htmlFor="ekpsCode" className="form-label">Код ЕКПС</label>
                <input
                  type="text"
                  className="form-control"
                  id="ekpsCode"
                  name="ekpsCode"
                  value={nomenclature.ekpsCode}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-4 mb-3">
                <label htmlFor="kvtCode" className="form-label">Код КВТ</label>
                <input
                  type="text"
                  className="form-control"
                  id="kvtCode"
                  name="kvtCode"
                  value={nomenclature.kvtCode}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-4 mb-3">
                <label htmlFor="drawingNumber" className="form-label">Номер чертежа</label>
                <input
                  type="text"
                  className="form-control"
                  id="drawingNumber"
                  name="drawingNumber"
                  value={nomenclature.drawingNumber}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            
            <div className="mb-3">
              <label htmlFor="typeOfNomenclature" className="form-label">Тип номенклатуры</label>
              <input
                type="text"
                className="form-control"
                id="typeOfNomenclature"
                name="typeOfNomenclature"
                value={nomenclature.typeOfNomenclature}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="d-flex justify-content-between mt-4">
              <div>
                <button type="submit" className="btn btn-primary me-2" disabled={loading}>
                  {loading ? 'Сохранение...' : 'Сохранить'}
                </button>
                <Link to="/nomenclature" className="btn btn-secondary">Отмена</Link>
              </div>
              
              {isEditMode && (
                <button 
                  type="button" 
                  className="btn btn-success" 
                  onClick={handleSendToIntBus} 
                  disabled={loading}
                >
                  <i className="fas fa-share-square me-1"></i>
                  {loading ? 'Отправка...' : 'Отправить в IntBus'}
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NomenclatureForm;
