import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const NomenclatureForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [nomenclature, setNomenclature] = useState({
    abbreviation: '',
    shortName: '',
    fullName: '',
    internalCode: '',
    cipher: '',
    ekpsCode: '',
    kvtCode: '',
    drawingNumber: '',
    typeOfNomenclature: ''
  });
  const [validationErrors, setValidationErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);

  const isEditMode = !!id;

  useEffect(() => {
    if (isEditMode) {
      fetchNomenclature();
    }
  }, [id]);

  const fetchNomenclature = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/nomenclature/${id}`);
      setNomenclature(response.data);
      setError(null);
    } catch (err) {
      setError('Ошибка при загрузке данных: ' + err.message);
      console.error('Ошибка при загрузке номенклатуры:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNomenclature(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Валидация обязательных полей
    const errors = {};
    if (!nomenclature.abbreviation.trim()) errors.abbreviation = 'Обязательное поле';
    if (!nomenclature.shortName.trim()) errors.shortName = 'Обязательное поле';
    if (!nomenclature.fullName.trim()) errors.fullName = 'Обязательное поле';
    if (!nomenclature.internalCode.trim()) errors.internalCode = 'Обязательное поле';
    
    if (Object.keys(errors).length > 0) {
      setValidationErrors(errors);
      return;
    }
    
    setIsSubmitting(true);
    setValidationErrors({});
    
    try {
      let response;
      
      // Установка правильных заголовков для запроса
      const headers = {
        'Content-Type': 'application/json'
      };
      
      if (isEditMode) {
        response = await axios.put(`/api/nomenclature/${id}`, nomenclature, { headers });
        setSaveStatus({ success: true, message: 'Номенклатура успешно обновлена!' });
      } else {
        response = await axios.post('/api/nomenclature', nomenclature, { headers });
        resetForm();
        setSaveStatus({ success: true, message: 'Номенклатура успешно создана!' });
      }
      
      setTimeout(() => {
        setSaveStatus(null);
      }, 3000);
    } catch (err) {
      console.error('Ошибка при сохранении номенклатуры:', err);
      
      // Улучшенная обработка ошибок
      let errorMessage = 'Ошибка при сохранении номенклатуры';
      
      if (err.response) {
        // Получаем данные ответа сервера
        if (typeof err.response.data === 'object') {
          errorMessage += `: ${JSON.stringify(err.response.data)}`;
        } else if (typeof err.response.data === 'string') {
          errorMessage += `: ${err.response.data}`;
        } else {
          errorMessage += `: Код ошибки ${err.response.status}`;
        }
      } else if (err.message) {
        errorMessage += `: ${err.message}`;
      }
      
      setSaveStatus({
        success: false,
        message: errorMessage
      });
    } finally {
      setIsSubmitting(false);
    }
  };
  
  const handleSendToIntBus = async () => {
    try {
      setLoading(true);
      setSuccessMessage(null);
      setError(null);
      
      const response = await axios.post(`/api/nomenclature/${id}/send-to-intbus`);
      setSuccessMessage('Данные успешно отправлены в IntBus');
    } catch (err) {
      setError('Ошибка при отправке данных в IntBus: ' + (err.response?.data || err.message));
      console.error('Ошибка при отправке данных в IntBus:', err);
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setNomenclature({
      abbreviation: '',
      shortName: '',
      fullName: '',
      internalCode: '',
      cipher: '',
      ekpsCode: '',
      kvtCode: '',
      drawingNumber: '',
      typeOfNomenclature: ''
    });
  };

  if (loading && isEditMode) {
    return <div className="d-flex justify-content-center mt-5">
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Загрузка...</span>
      </div>
    </div>;
  }

  return (
    <div className="container">
      <div className="row">
        <div className="col-12">
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item"><Link to="/">Главная</Link></li>
              <li className="breadcrumb-item"><Link to="/nomenclature">Номенклатура</Link></li>
              <li className="breadcrumb-item active" aria-current="page">
                {isEditMode ? `Редактирование #${id}` : 'Создание'}
              </li>
            </ol>
          </nav>
        </div>
      </div>

      <div className="card mb-4 shadow-sm">
        <div className="card-body">
          <h2 className="card-title mb-4">{isEditMode ? 'Редактирование номенклатуры' : 'Добавление новой номенклатуры'}</h2>
          
          {error && <div className="alert alert-danger" role="alert">{error}</div>}
          {successMessage && <div className="alert alert-success" role="alert">{successMessage}</div>}
          
          {saveStatus && (
            <div className={`alert ${saveStatus.success ? 'alert-success' : 'alert-danger'}`} role="alert">
              {saveStatus.message}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="abbreviation" className="form-label">Сокращение</label>
                <input
                  type="text"
                  className="form-control"
                  id="abbreviation"
                  name="abbreviation"
                  value={nomenclature.abbreviation}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="shortName" className="form-label">Краткое название</label>
                <input
                  type="text"
                  className="form-control"
                  id="shortName"
                  name="shortName"
                  value={nomenclature.shortName}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            
            <div className="mb-3">
              <label htmlFor="fullName" className="form-label">Полное название</label>
              <input
                type="text"
                className="form-control"
                id="fullName"
                name="fullName"
                value={nomenclature.fullName}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="internalCode" className="form-label">Внутренний код</label>
                <input
                  type="text"
                  className="form-control"
                  id="internalCode"
                  name="internalCode"
                  value={nomenclature.internalCode}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="cipher" className="form-label">Шифр</label>
                <input
                  type="text"
                  className="form-control"
                  id="cipher"
                  name="cipher"
                  value={nomenclature.cipher}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            
            <div className="row">
              <div className="col-md-4 mb-3">
                <label htmlFor="ekpsCode" className="form-label">Код ЕКПС</label>
                <input
                  type="text"
                  className="form-control"
                  id="ekpsCode"
                  name="ekpsCode"
                  value={nomenclature.ekpsCode}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-4 mb-3">
                <label htmlFor="kvtCode" className="form-label">Код КВТ</label>
                <input
                  type="text"
                  className="form-control"
                  id="kvtCode"
                  name="kvtCode"
                  value={nomenclature.kvtCode}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="col-md-4 mb-3">
                <label htmlFor="drawingNumber" className="form-label">Номер чертежа</label>
                <input
                  type="text"
                  className="form-control"
                  id="drawingNumber"
                  name="drawingNumber"
                  value={nomenclature.drawingNumber}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            
            <div className="mb-3">
              <label htmlFor="typeOfNomenclature" className="form-label">Тип номенклатуры</label>
              <input
                type="text"
                className="form-control"
                id="typeOfNomenclature"
                name="typeOfNomenclature"
                value={nomenclature.typeOfNomenclature}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="d-flex justify-content-between mt-4">
              <div>
                <button type="submit" className="btn btn-primary me-2" disabled={loading || isSubmitting}>
                  {loading ? 'Загрузка...' : isSubmitting ? 'Сохранение...' : 'Сохранить'}
                </button>
                <Link to="/nomenclature" className="btn btn-secondary">Отмена</Link>
              </div>
              
              {!isEditMode && (
                <button 
                  type="button" 
                  className="btn btn-outline-secondary" 
                  onClick={resetForm}
                  disabled={isSubmitting}
                >
                  Очистить форму
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NomenclatureForm;
